mapper.delete:
delete reply 
where cno = #{cno}
and	  password = #{password}

mapper.update:
update reply set 
		content = #{content}
where	cno = #{cno}
and		password = #{password}

mapper.select:

SELECT 	*
FROM	(
			select /*+ INDEX_DESC (reply idx_reply) */ 
					rownum rn, cno, content, writer, password, regDate, bno 
					from reply
					where bno = #{bno}
					and cno > 0
					and rownum <= #{cri.pageNum} * #{cri.amount}
		)
WHERE	rn > (#{cri.pageNum} - 1) * #{cri.amount}
